import React, { useState, useEffect } from 'react';
import { 
  TextField, Button, MenuItem, FormControl, InputLabel, Select, FormHelperText, 
  Grid, Typography, Box, Divider, Paper, Alert 
} from '@mui/material';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import dayjs from 'dayjs';
import 'dayjs/locale/de';

dayjs.locale('de');

const BookingForm = ({ onClose, onSave, booking = null, vehicles = [] }) => {
  const [formData, setFormData] = useState({
    vehicle_id: '',
    employee_name: '',
    department: '',
    purpose: '',
    start_date: dayjs(),
    end_date: dayjs().add(1, 'day'),
    status: 'Reserviert',
    notes: ''
  });
  
  const [formErrors, setFormErrors] = useState({});
  const [debugInfo, setDebugInfo] = useState({
    vehiclesCount: vehicles?.length || 0,
    initialBooking: booking || 'Keine Buchung übergeben',
    formChanges: 0
  });

  // Wenn eine Buchung zum Bearbeiten übergeben wurde, initialisiere das Formular
  useEffect(() => {
    if (booking) {
      console.log("Initialisiere Formular mit Buchungsdaten:", booking);
      setFormData({
        vehicle_id: booking.vehicle_id || '',
        employee_name: booking.employee_name || '',
        department: booking.department || '',
        purpose: booking.purpose || '',
        start_date: booking.start_date ? dayjs(booking.start_date) : dayjs(),
        end_date: booking.end_date ? dayjs(booking.end_date) : dayjs().add(1, 'day'),
        status: booking.status || 'Reserviert',
        notes: booking.notes || ''
      });
    }
  }, [booking]);

  // Debugging-Informationen aktualisieren, wenn sich die Fahrzeuge ändern
  useEffect(() => {
    setDebugInfo(prev => ({
      ...prev,
      vehiclesCount: vehicles?.length || 0
    }));
    
    // Wenn ein Fahrzeug übergeben wurde aber keine Fahrzeugliste, logge einen Warnung
    if (booking?.vehicle_id && (!vehicles || vehicles.length === 0)) {
      console.warn("Buchung hat eine Fahrzeug-ID, aber keine Fahrzeuge wurden übergeben", 
        { bookingVehicleId: booking.vehicle_id });
    }
  }, [vehicles, booking]);

  // Form Change Handler für Textfelder und Selects
  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`Feld ${name} geändert auf:`, value);
    
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Zurücksetzen des Fehlers für dieses Feld, wenn es einen gab
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: null
      }));
    }
    
    // Debug-Informationen aktualisieren
    setDebugInfo(prev => ({
      ...prev,
      formChanges: prev.formChanges + 1
    }));
  };

  // Date Change Handler
  const handleDateChange = (field, newValue) => {
    setFormData(prev => ({
      ...prev,
      [field]: newValue
    }));
    
    // Zurücksetzen des Fehlers für dieses Feld, wenn es einen gab
    if (formErrors[field]) {
      setFormErrors(prev => ({
        ...prev,
        [field]: null
      }));
    }
  };

  // Validiere das Formular vor dem Absenden
  const validateForm = () => {
    const errors = {};
    
    if (!formData.vehicle_id) {
      errors.vehicle_id = 'Bitte wählen Sie ein Fahrzeug aus';
    }
    
    if (!formData.employee_name) {
      errors.employee_name = 'Bitte geben Sie einen Mitarbeiternamen ein';
    }
    
    if (!formData.purpose) {
      errors.purpose = 'Bitte geben Sie einen Verwendungszweck an';
    }
    
    if (!formData.start_date) {
      errors.start_date = 'Bitte wählen Sie ein Startdatum';
    }
    
    if (!formData.end_date) {
      errors.end_date = 'Bitte wählen Sie ein Enddatum';
    } else if (formData.end_date && formData.start_date && formData.end_date.isBefore(formData.start_date)) {
      errors.end_date = 'Das Enddatum muss nach dem Startdatum liegen';
    }
    
    return errors;
  };

  // Form Submit Handler
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Formular wird abgeschickt mit Daten:", formData);
    
    // Validiere das Formular
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      console.log("Formularfehler:", errors);
      setFormErrors(errors);
      return;
    }
    
    // Formatiere die Daten für den API-Aufruf
    const bookingToSave = {
      ...formData,
      start_date: formData.start_date.toISOString(),
      end_date: formData.end_date.toISOString()
    };
    
    onSave(bookingToSave);
  };

  // Rendere das Formular
  return (
    <Box component="form" onSubmit={handleSubmit} noValidate>
      {/* Haupt-Formularinhalt */}
      <Grid container spacing={3}>
        {/* Fahrzeugauswahl */}
        <Grid item xs={12} md={6}>
          <FormControl fullWidth error={!!formErrors.vehicle_id} required>
            <InputLabel id="vehicle-label">Fahrzeug</InputLabel>
            <Select
              labelId="vehicle-label"
              id="vehicle_id"
              name="vehicle_id"
              value={formData.vehicle_id}
              onChange={handleChange}
              label="Fahrzeug"
            >
              <MenuItem value="" disabled>
                <em>Bitte wählen</em>
              </MenuItem>
              {vehicles && vehicles.length > 0 ? (
                vehicles.map((vehicle) => (
                  <MenuItem key={vehicle.id} value={vehicle.id}>
                    {vehicle.displayName || `${vehicle.modell} / ${vehicle.kennzeichen}`}
                  </MenuItem>
                ))
              ) : null}
            </Select>
            {formErrors.vehicle_id ? (
              <FormHelperText error>{formErrors.vehicle_id}</FormHelperText>
            ) : vehicles && vehicles.length === 0 ? (
              <FormHelperText error>
                Keine Fahrzeuge verfügbar. Bitte stellen Sie sicher, dass Fahrzeuge im System registriert sind.
              </FormHelperText>
            ) : null}
          </FormControl>
        </Grid>

        {/* Mitarbeiter */}
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Mitarbeiter"
            name="employee_name"
            value={formData.employee_name}
            onChange={handleChange}
            required
            error={!!formErrors.employee_name}
            helperText={formErrors.employee_name}
          />
        </Grid>

        {/* Abteilung/Kostenstelle */}
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Abteilung/Kostenstelle"
            name="department"
            value={formData.department}
            onChange={handleChange}
          />
        </Grid>

        {/* Zweck */}
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Verwendungszweck"
            name="purpose"
            value={formData.purpose}
            onChange={handleChange}
            required
            error={!!formErrors.purpose}
            helperText={formErrors.purpose}
          />
        </Grid>

        {/* Datumsauswahl */}
        <Grid item xs={12} md={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="de">
            <DateTimePicker
              label="Startdatum"
              value={formData.start_date}
              onChange={(newValue) => handleDateChange('start_date', newValue)}
              slotProps={{
                textField: { 
                  fullWidth: true,
                  required: true,
                  error: !!formErrors.start_date,
                  helperText: formErrors.start_date,
                  label: "Startdatum"
                }
              }}
            />
          </LocalizationProvider>
        </Grid>

        <Grid item xs={12} md={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="de">
            <DateTimePicker
              label="Enddatum"
              value={formData.end_date}
              onChange={(newValue) => handleDateChange('end_date', newValue)}
              slotProps={{
                textField: { 
                  fullWidth: true,
                  required: true,
                  error: !!formErrors.end_date,
                  helperText: formErrors.end_date,
                  label: "Enddatum"
                }
              }}
            />
          </LocalizationProvider>
        </Grid>

        {/* Status */}
        <Grid item xs={12} md={6}>
          <FormControl fullWidth>
            <InputLabel id="status-label">Status</InputLabel>
            <Select
              labelId="status-label"
              id="status"
              name="status"
              value={formData.status}
              onChange={handleChange}
              label="Status"
            >
              <MenuItem value="Reserviert">Reserviert</MenuItem>
              <MenuItem value="Ausgegeben">Ausgegeben</MenuItem>
              <MenuItem value="Zurückgegeben">Zurückgegeben</MenuItem>
            </Select>
          </FormControl>
        </Grid>

        {/* Notizen */}
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Notizen"
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            multiline
            rows={4}
          />
        </Grid>
      </Grid>

      {/* Aktionsbuttons */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 4 }}>
        <Button
          variant="outlined"
          onClick={onClose}
          sx={{ mr: 2 }}
        >
          Abbrechen
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
        >
          {booking ? 'Aktualisieren' : 'Buchung erstellen'}
        </Button>
      </Box>
      
      {/* Debug-Informationen - im Produktivmodus ausblenden */}
      {process.env.NODE_ENV !== 'production' && (
        <Paper sx={{ mt: 4, p: 2, backgroundColor: '#f8f8f8' }}>
          <Typography variant="subtitle2" gutterBottom>Debug-Informationen</Typography>
          <Divider sx={{ mb: 2 }} />
          
          <Typography variant="body2">Fahrzeuge: {debugInfo.vehiclesCount}</Typography>
          <Typography variant="body2">Formularänderungen: {debugInfo.formChanges}</Typography>
          
          {vehicles && vehicles.length > 0 && (
            <Box sx={{ mt: 1 }}>
              <Typography variant="body2" gutterBottom>Verfügbare Fahrzeuge:</Typography>
              <Box component="pre" sx={{ fontSize: '0.7rem', overflowX: 'auto', p: 1, backgroundColor: '#f1f1f1', borderRadius: 1 }}>
                {JSON.stringify(vehicles, null, 2)}
              </Box>
            </Box>
          )}
        </Paper>
      )}
    </Box>
  );
};

export default BookingForm;
