import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import VehicleList from './components/VehicleList';
import GlobalDashboard from './components/GlobalDashboard';
import StatisticsDashboard from './components/StatisticsDashboard';
import UserDashboard from './components/UserDashboard';
import VehicleRequests from './components/VehicleRequests';
import UserRequests from './components/UserRequests';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(true); // Für Demo-Zwecke standardmäßig angemeldet
  const [isAdmin, setIsAdmin] = useState(true); // Für Demo-Zwecke standardmäßig Admin
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeView, setActiveView] = useState('vehicles'); // Standard: Fahrzeugansicht anzeigen
  
  // Zentraler Fahrzeugzustand
  const [vehicles, setVehicles] = useState([]);
  const [vehiclesLoaded, setVehiclesLoaded] = useState(false);
  
  // Laden der Fahrzeuge beim Start der Anwendung
  useEffect(() => {
    loadVehicles();
  }, []);
  
  // Funktion zum Laden der Fahrzeuge aus dem localStorage
  const loadVehicles = () => {
    console.log("App: Lade Fahrzeuge...");
    
    // Standard-Demo-Fahrzeuge
    const standardVehicles = [
      {
        id: 1,
        modell: 'VW Golf',
        kennzeichen: 'M-AB 1234',
        kilometerstand: 45000,
        tankstand: 75,
        status: 'Verfügbar',
        tuev_datum: '2023-12-01',
        au_datum: '2023-12-01',
        bild: 'https://cdn.pixabay.com/photo/2013/07/13/10/15/volkswagen-156965_960_720.png'
      },
      {
        id: 2,
        modell: 'Mercedes C-Klasse',
        kennzeichen: 'M-CD 5678',
        kilometerstand: 25000,
        tankstand: 90,
        status: 'Verfügbar',
        tuev_datum: '2024-06-15',
        au_datum: '2024-06-15',
        bild: 'https://cdn.pixabay.com/photo/2016/04/17/22/10/mercedes-1335674_960_720.png'
      },
      {
        id: 3,
        modell: 'BMW 3er',
        kennzeichen: 'M-EF 9012',
        kilometerstand: 35000,
        tankstand: 60,
        status: 'Verfügbar',
        tuev_datum: '2024-03-20',
        au_datum: '2024-03-20',
        bild: 'https://cdn.pixabay.com/photo/2013/07/12/14/35/car-148539_960_720.png'
      },
      {
        id: 4,
        modell: 'Audi A4',
        kennzeichen: 'M-GH 3456',
        kilometerstand: 15000,
        tankstand: 100,
        status: 'Verfügbar',
        tuev_datum: '2024-09-10',
        au_datum: '2024-09-10',
        bild: 'https://cdn.pixabay.com/photo/2014/04/02/10/58/car-304920_960_720.png'
      },
      {
        id: 5,
        modell: 'Opel Astra',
        kennzeichen: 'M-IJ 7890',
        kilometerstand: 55000,
        tankstand: 45,
        status: 'Verfügbar',
        tuev_datum: '2023-10-05',
        au_datum: '2023-10-05',
        bild: 'https://cdn.pixabay.com/photo/2014/04/03/00/30/car-308405_960_720.png'
      }
    ];
    
    // Versuche, lokale Fahrzeuge zu laden
    try {
      const savedVehicles = localStorage.getItem('vehicles');
      if (savedVehicles) {
        const parsedVehicles = JSON.parse(savedVehicles);
        console.log("App: Geladene Fahrzeuge aus localStorage:", parsedVehicles);
        setVehicles(parsedVehicles);
      } else {
        // Wenn keine Fahrzeuge im localStorage existieren, die Standardfahrzeuge verwenden
        console.log("App: Keine Fahrzeuge im localStorage, verwende Standardfahrzeuge");
        localStorage.setItem('vehicles', JSON.stringify(standardVehicles));
        setVehicles(standardVehicles);
      }
    } catch (error) {
      console.error("App: Fehler beim Laden der Fahrzeuge aus localStorage:", error);
      setVehicles(standardVehicles);
    }
    
    setVehiclesLoaded(true);
  };
  
  // Fahrzeug hinzufügen
  const addVehicle = (newVehicle) => {
    console.log("App: Füge neues Fahrzeug hinzu:", newVehicle);
    const updatedVehicles = [...vehicles, newVehicle];
    setVehicles(updatedVehicles);
    
    // Speichern im localStorage
    try {
      localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
      console.log("App: Fahrzeuge im localStorage aktualisiert");
    } catch (error) {
      console.error("App: Fehler beim Speichern im localStorage:", error);
    }
  };
  
  // Fahrzeug aktualisieren
  const updateVehicle = (updatedVehicle) => {
    console.log("App: Aktualisiere Fahrzeug:", updatedVehicle);
    const updatedVehicles = vehicles.map(v => v.id === updatedVehicle.id ? updatedVehicle : v);
    setVehicles(updatedVehicles);
    
    // Speichern im localStorage
    try {
      localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
      console.log("App: Fahrzeuge im localStorage aktualisiert");
    } catch (error) {
      console.error("App: Fehler beim Speichern im localStorage:", error);
    }
  };
  
  // Fahrzeug löschen
  const deleteVehicle = (vehicleId) => {
    console.log("App: Lösche Fahrzeug mit ID:", vehicleId);
    const updatedVehicles = vehicles.filter(v => v.id !== vehicleId);
    setVehicles(updatedVehicles);
    
    // Speichern im localStorage
    try {
      localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
      console.log("App: Fahrzeuge im localStorage aktualisiert");
    } catch (error) {
      console.error("App: Fehler beim Speichern im localStorage:", error);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('isLoggedIn');
  };

  const toggleAdminMode = () => {
    setIsAdmin(!isAdmin);
  };

  // Renderlogik basierend auf der aktiven Ansicht
  const renderContent = () => {
    if (!vehiclesLoaded) {
      return <div className="w-full flex justify-center my-12">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>;
    }

    switch (activeView) {
      case 'dashboard':
        return isAdmin ? <GlobalDashboard /> : <UserDashboard />;
      case 'vehicles':
        return <VehicleList 
          isUserView={!isAdmin} 
          vehicles={vehicles} 
          onAddVehicle={addVehicle} 
          onUpdateVehicle={updateVehicle} 
          onDeleteVehicle={deleteVehicle} 
        />;
      case 'statistics':
        return isAdmin ? <StatisticsDashboard /> : null;
      case 'vehicle-requests':
        return isAdmin ? <VehicleRequests /> : null;
      case 'my-requests':
        return !isAdmin ? <UserRequests setActiveView={setActiveView} /> : null;
      default:
        return <VehicleList 
          isUserView={!isAdmin} 
          vehicles={vehicles} 
          onAddVehicle={addVehicle} 
          onUpdateVehicle={updateVehicle} 
          onDeleteVehicle={deleteVehicle} 
        />;
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar 
        isOpen={sidebarOpen} 
        toggleSidebar={() => setSidebarOpen(!sidebarOpen)} 
        isAdmin={isAdmin}
        onLogout={handleLogout}
        activeView={activeView}
        setActiveView={setActiveView}
      />
      
      <div className="flex-1 overflow-auto">
        <div className="p-4 md:p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-gray-800">Fahrzeugverwaltung</h1>
            
            <div className="flex items-center">
              <span className="mr-2 text-sm">{isAdmin ? 'Admin-Modus' : 'Benutzer-Modus'}</span>
              <button 
                onClick={toggleAdminMode}
                className="px-3 py-1 text-sm bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                {isAdmin ? 'Zu Benutzeransicht wechseln' : 'Zu Admin-Ansicht wechseln'}
              </button>
            </div>
          </div>
          
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default App;
