import React, { useState, useEffect } from 'react';
import axios from 'axios';
import dayjs from 'dayjs';
import 'dayjs/locale/de';
import { 
  Container, Box, Paper, Grid, Typography, 
  Divider, CircularProgress, Snackbar, Alert, 
  Table, TableHead, TableBody, TableRow, TableCell
} from '@mui/material';

dayjs.locale('de');

// Backend-URL
const API_URL = 'http://localhost:5000';

function Bookings() {
  // Zustandsvariablen
  const [loading, setLoading] = useState(true);
  const [loadingVehicles, setLoadingVehicles] = useState(true);
  const [vehicles, setVehicles] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [error, setError] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertSeverity, setAlertSeverity] = useState('info');

  // Formular-Zustand
  const [formData, setFormData] = useState({
    vehicle_id: '',
    employee_name: '',
    department: '',
    purpose: '',
    start_date: dayjs(),
    end_date: dayjs().add(1, 'day'),
    status: 'Reserviert',
    notes: ''
  });
  
  // Formularvalidierung
  const [formErrors, setFormErrors] = useState({});
  
  // Bearbeitung
  const [editingId, setEditingId] = useState(null);

  // Laden aller Daten beim Initialisieren der Komponente
  useEffect(() => {
    loadVehicles();
    loadBookings();
  }, []);

  // Fahrzeuge laden
  const loadVehicles = async () => {
    setLoadingVehicles(true);
    
    try {
      const response = await axios.get(`${API_URL}/api/vehicles`);
      
      if (Array.isArray(response.data)) {
        const formattedVehicles = response.data.map(vehicle => ({
          id: vehicle.id?.toString() || '',
          modell: vehicle.modell || 'Unbekannt',
          kennzeichen: vehicle.kennzeichen || 'Unbekannt',
          displayName: `${vehicle.modell || 'Unbekannt'} / ${vehicle.kennzeichen || 'Unbekannt'}`
        }));
        
        setVehicles(formattedVehicles);
      } else {
        console.error("Unerwartetes Datenformat für Fahrzeuge:", response.data);
        setVehicles([]);
        showNotification("Fahrzeugdaten haben ein unerwartetes Format", "warning");
      }
    } catch (error) {
      console.error("Fehler beim Laden der Fahrzeuge:", error);
      setVehicles([]);
      showNotification("Fehler beim Laden der Fahrzeuge: " + (error.message || "Unbekannter Fehler"), "error");
    } finally {
      setLoadingVehicles(false);
    }
  };

  // Buchungen laden
  const loadBookings = async () => {
    setLoading(true);
    
    try {
      const response = await axios.get(`${API_URL}/api/bookings`);
      
      if (Array.isArray(response.data)) {
        const formattedBookings = response.data.map(booking => ({
          ...booking,
          start_date: dayjs(booking.start_date),
          end_date: dayjs(booking.end_date)
        }));
        
        setBookings(formattedBookings);
      } else {
        console.error("Unerwartetes Datenformat für Buchungen:", response.data);
        setBookings([]);
        showNotification("Buchungsdaten haben ein unerwartetes Format", "warning");
      }
    } catch (error) {
      console.error("Fehler beim Laden der Buchungen:", error);
      setBookings([]);
      showNotification("Fehler beim Laden der Buchungen: " + (error.message || "Unbekannter Fehler"), "error");
    } finally {
      setLoading(false);
    }
  };

  // Form Change Handler
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Fehler zurücksetzen wenn Feld geändert wird
    if (formErrors[name]) {
      setFormErrors(prev => ({ ...prev, [name]: null }));
    }
  };

  // Datumsfeld-Handler
  const handleDateChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    
    // Fehler zurücksetzen wenn Feld geändert wird
    if (formErrors[field]) {
      setFormErrors(prev => ({ ...prev, [field]: null }));
    }
  };
  
  // Formular zurücksetzen
  const resetForm = () => {
    setFormData({
      vehicle_id: '',
      employee_name: '',
      department: '',
      purpose: '',
      start_date: dayjs(),
      end_date: dayjs().add(1, 'day'),
      status: 'Reserviert',
      notes: ''
    });
    setFormErrors({});
    setEditingId(null);
  };

  // Formular validieren
  const validateForm = () => {
    const errors = {};
    
    if (!formData.vehicle_id) {
      errors.vehicle_id = 'Bitte ein Fahrzeug auswählen';
    }
    
    if (!formData.employee_name) {
      errors.employee_name = 'Bitte einen Mitarbeiternamen eingeben';
    }
    
    if (!formData.purpose) {
      errors.purpose = 'Bitte einen Verwendungszweck angeben';
    }
    
    if (!formData.start_date) {
      errors.start_date = 'Bitte ein Startdatum wählen';
    }
    
    if (!formData.end_date) {
      errors.end_date = 'Bitte ein Enddatum wählen';
    } else if (formData.start_date && formData.end_date.isBefore(formData.start_date)) {
      errors.end_date = 'Das Enddatum muss nach dem Startdatum liegen';
    }
    
    return errors;
  };

  // Form Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Formular wird abgesendet mit Daten:", formData);
    
    // Formular validieren
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      // console.log("Formularfehler:", errors);
      setFormErrors(errors);
      return;
    }
    
    setLoading(true);
    
    try {
      // Anpassen der Daten an das Backend-Schema
      const bookingToSave = {
        vehicle_id: formData.vehicle_id,
        user_id: 1, // Default User-ID, falls benötigt
        startzeit: formData.start_date.toISOString(),
        endzeit: formData.end_date.toISOString(),
        status: formData.status || 'Reserviert',
        zweck: formData.purpose || '', 
        mitarbeiter: formData.employee_name || '',
        abteilung: formData.department || '',
        notizen: formData.notes || '',
        auto_groesse: ''  // Leeres Feld für auto_groesse
      };
      
      console.log("Angepasste Daten für Backend:", bookingToSave);
      
      if (editingId) {
        // Buchung aktualisieren
        const response = await axios.put(`${API_URL}/api/bookings/${editingId}`, bookingToSave);
        console.log("Aktualisierungsantwort:", response.data);
        showNotification("Buchung erfolgreich aktualisiert", "success");
      } else {
        // Neue Buchung erstellen
        const response = await axios.post(`${API_URL}/api/bookings`, bookingToSave);
        console.log("Erstellungsantwort:", response.data);
        showNotification("Buchung erfolgreich erstellt", "success");
      }
      
      // Formular zurücksetzen und Daten neu laden
      resetForm();
      await loadBookings();
    } catch (error) {
      console.error("Fehler beim Speichern der Buchung:", error);
      
      let errorMessage = "Fehler beim Speichern der Buchung";
      
      if (error.response) {
        // Der Server hat mit einem Statuscode außerhalb des 2xx-Bereichs geantwortet
        console.error("Fehlerantwort vom Server:", error.response.data);
        errorMessage += `: ${error.response.status} - ${error.response.data.message || error.response.statusText}`;
      } else if (error.request) {
        // Die Anfrage wurde gestellt, aber keine Antwort erhalten
        console.error("Keine Antwort vom Server erhalten");
        errorMessage += ": Keine Antwort vom Server";
      } else {
        // Beim Einrichten der Anfrage ist ein Fehler aufgetreten
        errorMessage += `: ${error.message}`;
      }
      
      showNotification(errorMessage, "error");
    } finally {
      setLoading(false);
    }
  };

  // Buchung löschen
  const handleDelete = async (id) => {
    if (!window.confirm("Möchten Sie diese Buchung wirklich löschen?")) {
      return;
    }
    
    setLoading(true);
    
    try {
      await axios.delete(`${API_URL}/api/bookings/${id}`);
      showNotification("Buchung erfolgreich gelöscht", "success");
      await loadBookings();
    } catch (error) {
      console.error("Fehler beim Löschen der Buchung:", error);
      showNotification("Fehler beim Löschen der Buchung: " + (error.message || "Unbekannter Fehler"), "error");
    } finally {
      setLoading(false);
    }
  };

  // Buchung zum Bearbeiten laden
  const handleEdit = (booking) => {
    // console.log("Lade Buchung zum Bearbeiten:", booking);
    setFormData({
      vehicle_id: booking.vehicle_id || '',
      employee_name: booking.employee_name || '',
      department: booking.department || '',
      purpose: booking.purpose || '',
      start_date: dayjs(booking.start_date),
      end_date: dayjs(booking.end_date),
      status: booking.status || 'Reserviert',
      notes: booking.notes || ''
    });
    setEditingId(booking.id);
    
    // Zum Formular scrollen
    document.getElementById('booking-form').scrollIntoView({ behavior: 'smooth' });
  };

  // Benachrichtigung anzeigen
  const showNotification = (message, severity = 'info') => {
    setAlertMessage(message);
    setAlertSeverity(severity);
    setShowAlert(true);
  };

  // Render-Funktion
  return (
    <Container maxWidth="lg" sx={{ py: 4, position: 'relative', overflow: 'visible' }}>
      {/* Überschrift */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" gutterBottom>Fahrzeugbuchungen</Typography>
        <Divider />
      </Box>
      
      {/* Hauptinhalt */}
      <Grid container spacing={4} sx={{ position: 'relative', zIndex: 1 }}>
        {/* Buchungsformular */}
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 3, position: 'relative', zIndex: 1 }} id="booking-form">
            <Typography variant="h5" gutterBottom>
              {editingId ? 'Buchung bearbeiten' : 'Neue Buchung erstellen'}
            </Typography>
            
            <Box component="form" onSubmit={handleSubmit} noValidate sx={{ position: 'relative' }}>
              <Grid container spacing={2}>
                {/* Fahrzeugauswahl */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="vehicle_id">
                      Fahrzeug *
                    </label>
                    <select
                      id="vehicle_id"
                      name="vehicle_id"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.vehicle_id}
                      onChange={handleInputChange}
                      required
                    >
                      <option value="" disabled>Bitte wählen</option>
                      {loadingVehicles ? (
                        <option value="" disabled>Fahrzeuge werden geladen...</option>
                      ) : vehicles.length > 0 ? (
                        vehicles.map(vehicle => (
                          <option key={vehicle.id} value={vehicle.id}>
                            {vehicle.displayName}
                          </option>
                        ))
                      ) : (
                        <option value="" disabled>Keine Fahrzeuge verfügbar</option>
                      )}
                    </select>
                    {formErrors.vehicle_id && (
                      <p className="mt-2 text-sm text-red-600">
                        {formErrors.vehicle_id}
                      </p>
                    )}
                    {!loadingVehicles && vehicles.length === 0 && (
                      <p className="mt-2 text-sm text-red-600">
                        Keine Fahrzeuge gefunden. Bitte fügen Sie zuerst Fahrzeuge hinzu.
                      </p>
                    )}
                  </div>
                </Grid>
                
                {/* Mitarbeiter */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="employee_name">
                      Mitarbeiter *
                    </label>
                    <input
                      type="text"
                      id="employee_name"
                      name="employee_name"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.employee_name}
                      onChange={handleInputChange}
                      required
                    />
                    {formErrors.employee_name && (
                      <p className="mt-2 text-sm text-red-600">
                        {formErrors.employee_name}
                      </p>
                    )}
                  </div>
                </Grid>
                
                {/* Abteilung */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="department">
                      Abteilung/Kostenstelle
                    </label>
                    <input
                      type="text"
                      id="department"
                      name="department"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.department}
                      onChange={handleInputChange}
                    />
                  </div>
                </Grid>
                
                {/* Zweck */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="purpose">
                      Verwendungszweck *
                    </label>
                    <input
                      type="text"
                      id="purpose"
                      name="purpose"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.purpose}
                      onChange={handleInputChange}
                      required
                    />
                    {formErrors.purpose && (
                      <p className="mt-2 text-sm text-red-600">
                        {formErrors.purpose}
                      </p>
                    )}
                  </div>
                </Grid>
                
                {/* Startdatum */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="start_date">
                      Startdatum *
                    </label>
                    <input
                      type="date"
                      id="start_date"
                      name="start_date"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.start_date.format('YYYY-MM-DD')}
                      onChange={(e) => {
                        const newDate = dayjs(e.target.value);
                        if(newDate.isValid()) {
                          // Behalte die Uhrzeit bei, ändere nur das Datum
                          const newDateTime = formData.start_date
                            .year(newDate.year())
                            .month(newDate.month())
                            .date(newDate.date());
                          handleDateChange('start_date', newDateTime);
                        }
                      }}
                      required
                    />
                    {formErrors.start_date && (
                      <p className="mt-2 text-sm text-red-600">
                        {formErrors.start_date}
                      </p>
                    )}
                  </div>
                </Grid>
                
                {/* Startzeit */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="start_time">
                      Startzeit
                    </label>
                    <input
                      type="time"
                      id="start_time"
                      name="start_time"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.start_date.format('HH:mm')}
                      onChange={(e) => {
                        const [hours, minutes] = e.target.value.split(':').map(Number);
                        const newDateTime = formData.start_date
                          .hour(hours)
                          .minute(minutes)
                          .second(0);
                        handleDateChange('start_date', newDateTime);
                      }}
                    />
                  </div>
                </Grid>
                
                {/* Enddatum */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="end_date">
                      Enddatum *
                    </label>
                    <input
                      type="date"
                      id="end_date"
                      name="end_date"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.end_date.format('YYYY-MM-DD')}
                      onChange={(e) => {
                        const newDate = dayjs(e.target.value);
                        if(newDate.isValid()) {
                          // Behalte die Uhrzeit bei, ändere nur das Datum
                          const newDateTime = formData.end_date
                            .year(newDate.year())
                            .month(newDate.month())
                            .date(newDate.date());
                          handleDateChange('end_date', newDateTime);
                        }
                      }}
                      required
                    />
                    {formErrors.end_date && (
                      <p className="mt-2 text-sm text-red-600">
                        {formErrors.end_date}
                      </p>
                    )}
                  </div>
                </Grid>
                
                {/* Endzeit */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="end_time">
                      Endzeit
                    </label>
                    <input
                      type="time"
                      id="end_time"
                      name="end_time"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.end_date.format('HH:mm')}
                      onChange={(e) => {
                        const [hours, minutes] = e.target.value.split(':').map(Number);
                        const newDateTime = formData.end_date
                          .hour(hours)
                          .minute(minutes)
                          .second(0);
                        handleDateChange('end_date', newDateTime);
                      }}
                    />
                  </div>
                </Grid>
                
                {/* Status */}
                <Grid item xs={12} sm={6}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="status">
                      Status
                    </label>
                    <select
                      id="status"
                      name="status"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.status}
                      onChange={handleInputChange}
                    >
                      <option value="Reserviert">Reserviert</option>
                      <option value="Ausgegeben">Ausgegeben</option>
                      <option value="Zurückgegeben">Zurückgegeben</option>
                    </select>
                  </div>
                </Grid>
                
                {/* Notizen */}
                <Grid item xs={12}>
                  <div className="form-control">
                    <label className="block text-gray-700 font-medium mb-2" htmlFor="notes">
                      Notizen
                    </label>
                    <textarea
                      id="notes"
                      name="notes"
                      rows="3"
                      className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                      value={formData.notes}
                      onChange={handleInputChange}
                    ></textarea>
                  </div>
                </Grid>
                
                {/* Buttons */}
                <Grid item xs={12}>
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={resetForm}
                      disabled={loading}
                      className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Abbrechen
                    </button>
                    <button
                      type="submit"
                      disabled={loading || loadingVehicles}
                      className="py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      {loading ? 'Wird gespeichert...' : editingId ? 'Aktualisieren' : 'Buchung erstellen'}
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        loadVehicles();
                        loadBookings();
                      }}
                      disabled={loading}
                      className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Daten aktualisieren
                    </button>
                  </div>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Grid>
        
        {/* Buchungsliste */}
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h5" gutterBottom>Buchungsübersicht</Typography>
            
            {loading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
                <CircularProgress />
              </Box>
            ) : bookings.length > 0 ? (
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Fahrzeug</TableCell>
                    <TableCell>Mitarbeiter</TableCell>
                    <TableCell>Zeitraum</TableCell>
                    <TableCell>Zweck</TableCell>
                    <TableCell>Status</TableCell>
                    <TableCell align="right">Aktionen</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {bookings.map((booking) => {
                    // Finde den Fahrzeugnamen anhand der ID
                    const vehicle = vehicles.find(v => v.id === booking.vehicle_id);
                    const vehicleName = vehicle ? vehicle.displayName : 'Unbekanntes Fahrzeug';
                    
                    return (
                      <TableRow key={booking.id} hover>
                        <TableCell>{vehicleName}</TableCell>
                        <TableCell>{booking.employee_name || 'Unbekannt'}</TableCell>
                        <TableCell>
                          {booking.start_date.format('DD.MM.YYYY HH:mm')} - {booking.end_date.format('DD.MM.YYYY HH:mm')}
                        </TableCell>
                        <TableCell>{booking.purpose || 'Kein Zweck angegeben'}</TableCell>
                        <TableCell>{booking.status || 'Unbekannt'}</TableCell>
                        <TableCell align="right">
                          <Button 
                            size="small" 
                            onClick={() => handleEdit(booking)}
                            sx={{ mr: 1 }}
                          >
                            Bearbeiten
                          </Button>
                          <Button 
                            size="small" 
                            color="error" 
                            onClick={() => handleDelete(booking.id)}
                          >
                            Löschen
                          </Button>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            ) : (
              <Box sx={{ textAlign: 'center', p: 3 }}>
                <Typography color="textSecondary">Keine Buchungen vorhanden</Typography>
              </Box>
            )}
          </Paper>
        </Grid>
      </Grid>
      
      {/* Benachrichtigungen */}
      <Snackbar 
        open={showAlert} 
        autoHideDuration={6000} 
        onClose={() => setShowAlert(false)}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
        sx={{ zIndex: 10000 }}
      >
        <Alert onClose={() => setShowAlert(false)} severity={alertSeverity} sx={{ width: '100%' }}>
          {alertMessage}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default Bookings;
