import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VehicleDetail from './VehicleDetail';

function VehicleList({ isUserView = false }) {
  const [vehicles, setVehicles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newVehicle, setNewVehicle] = useState({
    modell: '',
    kennzeichen: '',
    kilometerstand: 0,
    tankstand: 100,
    status: 'Verfügbar',
    bild: ''
  });
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [showVehicleDetail, setShowVehicleDetail] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [expanded, setExpanded] = useState({});
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [vehicleToRequest, setVehicleToRequest] = useState(null);
  const [requestDetails, setRequestDetails] = useState({
    startDate: '',
    endDate: '',
    purpose: ''
  });

  useEffect(() => {
    fetchVehicles();
  }, []);

  const fetchVehicles = () => {
    console.log('Starte Fahrzeug-Abfrage...');
    
    // Demo-Fahrzeuge direkt laden, ohne auf einen API-Fehler zu warten
    const demoVehicles = [
      {
        id: 1,
        modell: 'VW Golf',
        kennzeichen: 'M-AB 1234',
        kilometerstand: 45000,
        tankstand: 75,
        status: 'Verfügbar',
        tuev_datum: '2023-12-01',
        au_datum: '2023-12-01',
        bild: 'https://via.placeholder.com/800x400?text=VW+Golf'
      },
      {
        id: 2,
        modell: 'Mercedes C-Klasse',
        kennzeichen: 'M-CD 5678',
        kilometerstand: 25000,
        tankstand: 90,
        status: 'Verfügbar',
        tuev_datum: '2024-06-15',
        au_datum: '2024-06-15',
        bild: 'https://via.placeholder.com/800x400?text=Mercedes+C-Klasse'
      },
      {
        id: 3,
        modell: 'BMW 3er',
        kennzeichen: 'M-EF 9012',
        kilometerstand: 35000,
        tankstand: 60,
        status: 'Verfügbar', // Auf Verfügbar gesetzt
        tuev_datum: '2024-03-20',
        au_datum: '2024-03-20',
        bild: 'https://via.placeholder.com/800x400?text=BMW+3er'
      },
      {
        id: 4,
        modell: 'Audi A4',
        kennzeichen: 'M-GH 3456',
        kilometerstand: 15000,
        tankstand: 100,
        status: 'Verfügbar',
        tuev_datum: '2024-09-10',
        au_datum: '2024-09-10',
        bild: 'https://via.placeholder.com/800x400?text=Audi+A4'
      },
      {
        id: 5,
        modell: 'Opel Astra',
        kennzeichen: 'M-IJ 7890',
        kilometerstand: 55000,
        tankstand: 45,
        status: 'Verfügbar', // Auf Verfügbar gesetzt
        tuev_datum: '2023-10-05',
        au_datum: '2023-10-05',
        bild: 'https://via.placeholder.com/800x400?text=Opel+Astra'
      }
    ];

    // Versuche, lokal gespeicherte Fahrzeuge zu laden
    let localVehicles = [];
    try {
      const savedVehicles = localStorage.getItem('vehicles');
      if (savedVehicles) {
        localVehicles = JSON.parse(savedVehicles);
        console.log('Lokale Fahrzeuge geladen:', localVehicles);
      }
    } catch (error) {
      console.error('Fehler beim Laden lokaler Fahrzeuge:', error);
    }

    // Kombiniere Demo- und lokale Fahrzeuge
    const allVehicles = [...demoVehicles, ...localVehicles];
    
    console.log('Alle Fahrzeuge (Demo + Lokal):', allVehicles);
    console.log('Demo-Fahrzeuge Status:', allVehicles.map(v => `${v.modell}: ${v.status}`));
    
    setVehicles(allVehicles);
    setIsLoading(false);

    // Trotzdem versuchen, vom API zu laden (für zukünftige Implementierungen)
    axios.get('http://localhost:5000/api/vehicles')
      .then(response => {
        console.log('Antwort vom Server:', response);
        
        // Debug-Ausgabe für TÜV und AU Daten
        if (response.data && Array.isArray(response.data)) {
          response.data.forEach(vehicle => {
            console.log(`Fahrzeug ${vehicle.id}: Model=${vehicle.modell}, TÜV=${vehicle.tuev_datum}, AU=${vehicle.au_datum}`);
          });
          
          // Setze nur, wenn erfolgreich geladen
          setVehicles(response.data);
        }
      })
      .catch(error => {
        console.error('Fehler beim Laden der Fahrzeuge vom API:', error);
        // Kein setVehicles hier, da wir bereits die Demo-Fahrzeuge geladen haben
      });
  };

  const handleAddVehicle = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const formData = new FormData();
      Object.keys(newVehicle).forEach(key => {
        if (key === 'bild' && typeof newVehicle.bild === 'object') {
          formData.append('bild', newVehicle.bild);
        } else {
          formData.append(key, newVehicle[key]);
        }
      });

      const response = await axios.post('http://localhost:5000/api/vehicles', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Fahrzeug hinzugefügt:', response.data);
      setVehicles(prev => [...prev, response.data]);
      setShowAddForm(false);
      setNewVehicle({
        modell: '',
        kennzeichen: '',
        kilometerstand: 0,
        tankstand: 100,
        status: 'Verfügbar',
        bild: ''
      });
    } catch (error) {
      console.error('Error adding vehicle:', error);
      
      // Fallback: Demo-Fahrzeug erstellen, wenn API nicht verfügbar
      console.log('Fallback: Erstelle Demo-Fahrzeug im Offline-Modus');
      
      // Bild-URL erstellen (entweder lokale Datei oder Platzhalterbild)
      let bildURL = 'https://via.placeholder.com/800x400?text=' + encodeURIComponent(newVehicle.modell);
      
      // Demo-Fahrzeug mit generierten Daten
      const newId = Date.now();
      const demoVehicle = {
        id: newId,
        ...newVehicle,
        status: 'Verfügbar', // Sicherstellen, dass das Fahrzeug verfügbar ist
        bild: bildURL
      };
      
      // Fahrzeug zur Anzeige hinzufügen
      setVehicles(prev => [...prev, demoVehicle]);
      
      // Fahrzeug auch im localStorage speichern
      try {
        const savedVehicles = JSON.parse(localStorage.getItem('vehicles') || '[]');
        savedVehicles.push(demoVehicle);
        localStorage.setItem('vehicles', JSON.stringify(savedVehicles));
        console.log('Fahrzeug in localStorage gespeichert:', demoVehicle);
      } catch (storageError) {
        console.error('Fehler beim Speichern im localStorage:', storageError);
      }
      
      setShowAddForm(false);
      setNewVehicle({
        modell: '',
        kennzeichen: '',
        kilometerstand: 0,
        tankstand: 100,
        status: 'Verfügbar',
        bild: ''
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Vorschau für das Bild anzeigen
  const handleImageSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewVehicle({ ...newVehicle, bild: file });
    }
  };

  // Fahrzeug zum Anzeigen auswählen
  const handleSelectVehicle = (vehicle) => {
    setSelectedVehicle(vehicle);
    setShowVehicleDetail(true);
  };

  // Fahrzeug aktualisieren
  const handleVehicleUpdate = (updatedVehicle) => {
    setVehicles(prev => 
      prev.map(v => v.id === updatedVehicle.id ? updatedVehicle : v)
    );
    setSelectedVehicle(updatedVehicle);
  };

  // Fahrzeug löschen
  const handleVehicleDelete = (vehicleId) => {
    setVehicles(prev => prev.filter(v => v.id !== vehicleId));
    setShowVehicleDetail(false);
  };

  // Fahrzeug anfragen
  const handleRequestVehicle = (vehicle) => {
    console.log("handleRequestVehicle aufgerufen für Fahrzeug:", vehicle);
    console.log("Fahrzeug Status:", vehicle.status);
    
    // Überprüfen, ob das Fahrzeug verfügbar ist
    if (vehicle.status !== 'Verfügbar' && vehicle.status !== undefined) {
      alert('Dieses Fahrzeug ist derzeit nicht verfügbar.');
      return;
    }
    
    setVehicleToRequest(vehicle);
    setShowConfirmation(true);
  };

  // Anfrage bestätigen
  const handleConfirmRequest = () => {
    if (!requestDetails.startDate || !requestDetails.endDate || !requestDetails.purpose) {
      alert('Bitte füllen Sie alle Felder aus.');
      return;
    }

    // Anfrage im Local Storage speichern
    const existingRequests = JSON.parse(localStorage.getItem('vehicleRequests') || '[]');
    const newRequest = {
      id: Date.now().toString(),
      vehicleId: vehicleToRequest.id,
      vehicleModel: vehicleToRequest.modell,
      licensePlate: vehicleToRequest.kennzeichen,
      startDate: requestDetails.startDate,
      endDate: requestDetails.endDate,
      purpose: requestDetails.purpose,
      status: 'Ausstehend',
      timestamp: new Date().toISOString(),
      userId: '1', // In einer realen Anwendung würde hier die ID des angemeldeten Benutzers stehen
      userName: 'Max Mustermann' // In einer realen Anwendung würde hier der Name des angemeldeten Benutzers stehen
    };

    existingRequests.push(newRequest);
    localStorage.setItem('vehicleRequests', JSON.stringify(existingRequests));

    // Dialog schließen und Formular zurücksetzen
    setShowConfirmation(false);
    setVehicleToRequest(null);
    setRequestDetails({
      startDate: '',
      endDate: '',
      purpose: ''
    });

    alert('Fahrzeuganfrage wurde erfolgreich gesendet!');
  };

  // CSV-Export
  const exportToCSV = () => {
    // Header für CSV
    let csvContent = "Modell,Kennzeichen,Kilometerstand,Tankstand,Status,TÜV-Datum,AU-Datum\n";
    
    // Daten hinzufügen
    vehicles.forEach(vehicle => {
      const tuevDatum = vehicle.tuev_datum ? new Date(vehicle.tuev_datum).toLocaleDateString() : '';
      const auDatum = vehicle.au_datum ? new Date(vehicle.au_datum).toLocaleDateString() : '';
      
      csvContent += `${vehicle.modell},${vehicle.kennzeichen},${vehicle.kilometerstand},${vehicle.tankstand}%,${vehicle.status},${tuevDatum},${auDatum}\n`;
    });
    
    // CSV-Datei erstellen und herunterladen
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.setAttribute('href', url);
    link.setAttribute('download', 'fahrzeuge.csv');
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleToggleExpand = (vehicleId) => {
    setExpanded(prev => ({
      ...prev,
      [vehicleId]: !prev[vehicleId]
    }));
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Nicht eingetragen';
    return new Date(dateString).toLocaleDateString('de-DE');
  };

  // Filtern der Fahrzeuge basierend auf der Sucheingabe
  const filteredVehicles = vehicles.filter(vehicle =>
    vehicle.modell.toLowerCase().includes(searchTerm.toLowerCase()) ||
    vehicle.kennzeichen.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6">
        <h2 className="text-2xl font-bold mb-4 md:mb-0">{isUserView ? "Verfügbare Fahrzeuge" : "Fahrzeuge"}</h2>
        <div className="flex flex-col md:flex-row space-y-2 md:space-y-0 md:space-x-4 w-full md:w-auto">
          <div className="relative w-full md:w-64">
            <input
              type="text"
              className="w-full p-2 border rounded pl-10"
              placeholder="Suchen..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div className="absolute left-3 top-2.5 text-gray-400">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
            </div>
          </div>
          {!isUserView && (
            <>
              <button
                onClick={() => setShowAddForm(true)}
                className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded flex items-center justify-center"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
                </svg>
                Fahrzeug hinzufügen
              </button>
              <button
                onClick={exportToCSV}
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded flex items-center justify-center"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm3.293-7.707a1 1 0 011.414 0L9 10.586V3a1 1 0 112 0v7.586l1.293-1.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
                </svg>
                Als CSV exportieren
              </button>
            </>
          )}
        </div>
      </div>

      {isLoading ? (
        <div className="w-full flex justify-center my-12">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredVehicles.map(vehicle => (
            <div
              key={vehicle.id}
              className="border rounded-lg overflow-hidden hover:shadow-lg transition-shadow duration-300"
            >
              <div className="relative h-48 bg-gray-200">
                {vehicle.bild ? (
                  <img
                    src={vehicle.bild.startsWith('http') 
                      ? vehicle.bild 
                      : `http://localhost:5000/api/uploads/vehicles/${vehicle.bild}`}
                    alt={vehicle.modell}
                    className="w-full h-full object-cover"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = 'https://via.placeholder.com/400x200?text=Kein+Bild';
                    }}
                  />
                ) : (
                  <div className="flex items-center justify-center h-full">
                    <span className="text-gray-400">Kein Bild verfügbar</span>
                  </div>
                )}
                <div className={`absolute top-2 right-2 px-2 py-1 rounded text-xs text-white ${
                  vehicle.status === 'Verfügbar' ? 'bg-green-500' :
                  vehicle.status === 'In Benutzung' ? 'bg-blue-500' :
                  vehicle.status === 'In Wartung' ? 'bg-yellow-500' : 
                  'bg-red-500'
                }`}>
                  {vehicle.status || 'Verfügbar'}
                </div>
              </div>
              
              <div className="p-4 space-y-4">
                <div className="flex justify-between items-start">
                  <h3 className="text-xl font-semibold">{vehicle.modell}</h3>
                  <span className="font-medium text-gray-500">{vehicle.kennzeichen}</span>
                </div>
                
                <div>
                  <div className="grid grid-cols-2 gap-2 text-sm">
                    <div>
                      <p className="text-gray-500">Kilometerstand</p>
                      <p className="font-medium">{vehicle.kilometerstand?.toLocaleString() || '0'} km</p>
                    </div>
                    <div>
                      <p className="text-gray-500">Tankstand</p>
                      <p className="font-medium">{vehicle.tankstand || '100'}%</p>
                    </div>
                  </div>
                </div>
                
                {expanded[vehicle.id] && (
                  <div className="mt-4 space-y-2 border-t pt-4">
                    <div className="grid grid-cols-2 gap-2 text-sm">
                      <div>
                        <p className="text-gray-500">TÜV bis</p>
                        <p className="font-medium">{formatDate(vehicle.tuev_datum)}</p>
                      </div>
                      <div>
                        <p className="text-gray-500">AU bis</p>
                        <p className="font-medium">{formatDate(vehicle.au_datum)}</p>
                      </div>
                    </div>
                  </div>
                )}
                
                <div className="flex justify-between mt-4">
                  <button
                    onClick={() => handleToggleExpand(vehicle.id)}
                    className="text-blue-500 hover:text-blue-700 flex items-center"
                  >
                    {expanded[vehicle.id] ? 'Weniger anzeigen' : 'Mehr anzeigen'}
                    {expanded[vehicle.id] ? (
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 ml-1" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clipRule="evenodd" />
                      </svg>
                    ) : (
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 ml-1" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M14.707 12.707a1 1 0 01-1.414 0L10 9.414l-3.293 3.293a1 1 0 01-1.414-1.414l4-4a1 1 0 011.414 0l4 4a1 1 0 010 1.414z" clipRule="evenodd" />
                      </svg>
                    )}
                  </button>
                  <div className="space-x-2">
                    {isUserView ? (
                      <button
                        onClick={(e) => {
                          e.stopPropagation(); // Verhindert, dass das Event auf das Elternelement weitergeleitet wird
                          console.log("Anfrage-Button geklickt für Fahrzeug:", vehicle);
                          console.log("Fahrzeug-Status:", vehicle.status);
                          handleRequestVehicle(vehicle);
                        }}
                        className={`px-3 py-1 text-white rounded ${
                          vehicle.status === 'Verfügbar' 
                            ? 'bg-blue-500 hover:bg-blue-600' 
                            : 'bg-gray-400 cursor-not-allowed'
                        }`}
                        disabled={vehicle.status !== 'Verfügbar' && vehicle.status !== undefined}
                      >
                        {vehicle.status === 'Verfügbar' ? 'Anfragen' : 'Nicht verfügbar'}
                      </button>
                    ) : (
                      <button
                        onClick={() => handleSelectVehicle(vehicle)}
                        className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                      >
                        Details
                      </button>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Fahrzeugdetail-Modal */}
      {showVehicleDetail && selectedVehicle && (
        <VehicleDetail 
          vehicle={selectedVehicle} 
          onClose={() => setShowVehicleDetail(false)} 
          onUpdate={handleVehicleUpdate}
          onDelete={handleVehicleDelete}
        />
      )}

      {/* Anfrage-Bestätigungsdialog */}
      {showConfirmation && vehicleToRequest && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <h3 className="text-lg font-bold mb-4">Fahrzeug anfragen</h3>
            <p className="mb-4">Sie möchten das Fahrzeug <strong>{vehicleToRequest.modell}</strong> ({vehicleToRequest.kennzeichen}) anfragen.</p>
            
            <form className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Startdatum</label>
                <input
                  type="date"
                  className="w-full p-2 border rounded"
                  value={requestDetails.startDate}
                  onChange={(e) => setRequestDetails({...requestDetails, startDate: e.target.value})}
                  required
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Enddatum</label>
                <input
                  type="date"
                  className="w-full p-2 border rounded"
                  value={requestDetails.endDate}
                  onChange={(e) => setRequestDetails({...requestDetails, endDate: e.target.value})}
                  required
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Verwendungszweck</label>
                <textarea
                  className="w-full p-2 border rounded"
                  rows="3"
                  value={requestDetails.purpose}
                  onChange={(e) => setRequestDetails({...requestDetails, purpose: e.target.value})}
                  required
                ></textarea>
              </div>
              
              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  onClick={() => {
                    setShowConfirmation(false);
                    setVehicleToRequest(null);
                  }}
                  className="px-4 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400"
                >
                  Abbrechen
                </button>
                <button
                  type="button"
                  onClick={handleConfirmRequest}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Anfrage senden
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Formular zum Hinzufügen eines Fahrzeugs */}
      {showAddForm && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
          <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-1/2 shadow-lg rounded-md bg-white">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-semibold">Neues Fahrzeug hinzufügen</h3>
              <button
                onClick={() => setShowAddForm(false)}
                className="text-gray-500 hover:text-gray-700"
              >
                <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            
            <form onSubmit={handleAddVehicle} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Modell</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded"
                    value={newVehicle.modell}
                    onChange={(e) => setNewVehicle({...newVehicle, modell: e.target.value})}
                    required
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Kennzeichen</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded"
                    value={newVehicle.kennzeichen}
                    onChange={(e) => setNewVehicle({...newVehicle, kennzeichen: e.target.value})}
                    required
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Kilometerstand</label>
                  <input
                    type="number"
                    className="w-full p-2 border rounded"
                    value={newVehicle.kilometerstand}
                    onChange={(e) => setNewVehicle({...newVehicle, kilometerstand: parseInt(e.target.value) || 0})}
                    required
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Tankstand (%)</label>
                  <input
                    type="number"
                    min="0"
                    max="100"
                    className="w-full p-2 border rounded"
                    value={newVehicle.tankstand}
                    onChange={(e) => setNewVehicle({...newVehicle, tankstand: parseInt(e.target.value) || 0})}
                    required
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
                  <select
                    className="w-full p-2 border rounded"
                    value={newVehicle.status}
                    onChange={(e) => setNewVehicle({...newVehicle, status: e.target.value})}
                    required
                  >
                    <option value="Verfügbar">Verfügbar</option>
                    <option value="In Benutzung">In Benutzung</option>
                    <option value="In Wartung">In Wartung</option>
                    <option value="Außer Betrieb">Außer Betrieb</option>
                  </select>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Fahrzeugbild</label>
                  <input
                    type="file"
                    className="w-full p-2 border rounded"
                    onChange={handleImageSelect}
                    accept="image/*"
                  />
                </div>
              </div>
              
              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  onClick={() => setShowAddForm(false)}
                  className="px-4 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400"
                >
                  Abbrechen
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
                >
                  Fahrzeug hinzufügen
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default VehicleList;
