import React, { useState } from 'react';
import axios from 'axios';

function DamageReport({ vehicleId, onClose, onDamageReported, editingReport = null }) {
  const [description, setDescription] = useState(editingReport?.description || '');
  const [images, setImages] = useState([]);
  const [status, setStatus] = useState(editingReport?.status || 'Gemeldet');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formData = new FormData();
    formData.append('description', description);
    formData.append('status', status);
    images.forEach(image => {
      formData.append('images', image);
    });

    try {
      if (editingReport) {
        // Bearbeiten
        formData.append('keep_images', 'true'); // Behalte existierende Bilder
        await axios.put(
          `http://localhost:5000/api/vehicles/${vehicleId}/damage-reports/${editingReport.id}`,
          formData,
          {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          }
        );
      } else {
        // Neu erstellen
        await axios.post(
          `http://localhost:5000/api/vehicles/${vehicleId}/damage-reports`,
          formData,
          {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          }
        );
      }
      onDamageReported();
      onClose();
    } catch (error) {
      console.error('Fehler beim Melden des Schadens:', error);
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4">
          {editingReport ? 'Schadensmeldung bearbeiten' : 'Neue Schadensmeldung'}
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Beschreibung des Schadens
            </label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              rows="4"
              required
            />
          </div>
          
          {editingReport && (
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Status
              </label>
              <select
                value={status}
                onChange={(e) => setStatus(e.target.value)}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              >
                <option value="Gemeldet">Gemeldet</option>
                <option value="In Bearbeitung">In Bearbeitung</option>
                <option value="Repariert">Repariert</option>
              </select>
            </div>
          )}
          
          <div>
            <label className="block text-sm font-medium text-gray-700">
              {editingReport ? 'Weitere Bilder hochladen' : 'Bilder hochladen'}
            </label>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={(e) => setImages(Array.from(e.target.files))}
              className="mt-1 block w-full"
            />
          </div>

          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
            >
              Abbrechen
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
            >
              {editingReport ? 'Aktualisieren' : 'Schaden melden'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default DamageReport;
