import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import VehicleList from './components/VehicleList';
import GlobalDashboard from './components/GlobalDashboard';
import StatisticsDashboard from './components/StatisticsDashboard';
import Bookings from './components/Bookings';
import UserDashboard from './components/UserDashboard';
import VehicleRequest from './components/VehicleRequest';

function App() {
  const [activeView, setActiveView] = useState('dashboard');
  // Benutzerrolle: 'admin' oder 'user'
  const [userRole, setUserRole] = useState('admin');

  // Simulieren einer Benutzerauthentifizierung
  useEffect(() => {
    // In einer echten Anwendung würde hier eine API-Anfrage zur Authentifizierung erfolgen
    // Für Demo-Zwecke simulieren wir eine erfolgreiche Anmeldung mit einer vordefinierten Rolle
    const simulatedRole = localStorage.getItem('userRole') || 'admin';
    setUserRole(simulatedRole);

    // Demo-Zweck: Rolle über die Konsole ändern
    console.log('Zum Ändern der Rolle: localStorage.setItem("userRole", "user") oder "admin", dann Seite neu laden');
  }, []);

  // Toggle-Funktion zum Wechseln der Benutzerrolle (nur für Demo-Zwecke)
  const toggleRole = () => {
    const newRole = userRole === 'admin' ? 'user' : 'admin';
    setUserRole(newRole);
    localStorage.setItem('userRole', newRole);
  };

  // Menüoptionen basierend auf der Benutzerrolle
  const getMenuItems = () => {
    if (userRole === 'admin') {
      return [
        { id: 'dashboard', name: 'Dashboard', icon: 'HomeIcon' },
        { id: 'vehicles', name: 'Fahrzeuge', icon: 'TruckIcon' },
        { id: 'booking', name: 'Buchungen', icon: 'CalendarIcon' },
        { id: 'statistics', name: 'Statistiken', icon: 'ChartBarIcon' },
      ];
    } else {
      return [
        { id: 'user-dashboard', name: 'Dashboard', icon: 'HomeIcon' },
        { id: 'vehicles', name: 'Fahrzeuge', icon: 'TruckIcon' },
        { id: 'vehicle-request', name: 'Fahrzeug anfragen', icon: 'PlusCircleIcon' },
        { id: 'my-bookings', name: 'Meine Buchungen', icon: 'CalendarIcon' },
      ];
    }
  };

  // Content basierend auf aktiver Ansicht und Benutzerrolle rendern
  const renderContent = () => {
    // Für Administrator-Ansichten
    if (userRole === 'admin') {
      switch(activeView) {
        case 'dashboard':
          return <GlobalDashboard />;
        case 'vehicles':
          return <VehicleList />;
        case 'booking':
          return <Bookings />;
        case 'statistics':
          return <StatisticsDashboard />;
        default:
          return <GlobalDashboard />;
      }
    } 
    // Für Benutzer-Ansichten
    else {
      switch(activeView) {
        case 'user-dashboard':
          return <UserDashboard />;
        case 'vehicles':
          return <VehicleList isUserView={true} />;
        case 'vehicle-request':
          return <VehicleRequest />;
        case 'my-bookings':
          return <Bookings isUserView={true} />;
        default:
          return <UserDashboard />;
      }
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar 
        activeView={activeView} 
        setActiveView={setActiveView} 
        menuItems={getMenuItems()}
      />
      <main className="flex-1 overflow-y-auto p-8" style={{ overflow: 'visible' }}>
        <div className="max-w-7xl mx-auto">
          {/* Rollenumschalter (nur für Demo-Zwecke) */}
          <div className="flex justify-end mb-4">
            <button 
              onClick={toggleRole} 
              className="text-sm bg-gray-200 px-3 py-1 rounded hover:bg-gray-300"
            >
              Rolle: {userRole === 'admin' ? 'Administrator' : 'Benutzer'} (Klicken zum Wechseln)
            </button>
          </div>
          
          {renderContent()}
        </div>
      </main>
    </div>
  );
}

export default App;
