import React, { useState, useEffect } from 'react';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import dayjs from 'dayjs';
import 'dayjs/locale/de';
import { TextField, Button, MenuItem, FormControl, InputLabel, Select, FormHelperText, Grid, Paper } from '@mui/material';

dayjs.locale('de');

const BookingForm = ({ onClose, onSave, booking = null, vehicles = [] }) => {
  console.log("BookingForm geladen mit Fahrzeugen:", vehicles);
  console.log("Zu bearbeitende Buchung:", booking);

  // Initialer Formularstatus
  const [formData, setFormData] = useState({
    vehicle_id: '',
    employee_name: '',
    department: '',
    purpose: '',
    start_date: dayjs(),
    end_date: dayjs().add(1, 'day'),
    status: 'Reserviert',
    notes: ''
  });

  // Wenn eine Buchung zum Bearbeiten übergeben wurde, initialisiere das Formular mit ihren Daten
  useEffect(() => {
    if (booking) {
      console.log("Initialisiere Formular mit Buchungsdaten:", booking);
      setFormData({
        vehicle_id: booking.vehicle_id || '',
        employee_name: booking.employee_name || '',
        department: booking.department || '',
        purpose: booking.purpose || '',
        start_date: booking.start_date ? dayjs(booking.start_date) : dayjs(),
        end_date: booking.end_date ? dayjs(booking.end_date) : dayjs().add(1, 'day'),
        status: booking.status || 'Reserviert',
        notes: booking.notes || ''
      });
    }
  }, [booking]);

  // Form Change Handler
  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`Feld ${name} geändert auf:`, value);
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Date Change Handler
  const handleDateChange = (field, newValue) => {
    setFormData({
      ...formData,
      [field]: newValue
    });
  };

  // Form Submit Handler
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Formular wird abgeschickt mit Daten:", formData);
    
    // Formatiere die Daten für den API-Aufruf
    const bookingToSave = {
      ...formData,
      start_date: formData.start_date.toISOString(),
      end_date: formData.end_date.toISOString()
    };
    
    onSave(bookingToSave);
  };

  // Formularanzeige
  return (
    <form onSubmit={handleSubmit}>
      <Grid container spacing={3}>
        {/* Fahrzeugauswahl */}
        <Grid item xs={12} md={6}>
          <FormControl fullWidth required>
            <InputLabel id="vehicle-label">Fahrzeug</InputLabel>
            <Select
              labelId="vehicle-label"
              id="vehicle_id"
              name="vehicle_id"
              value={formData.vehicle_id}
              onChange={handleChange}
              label="Fahrzeug"
            >
              <MenuItem value="" disabled>
                <em>Bitte wählen</em>
              </MenuItem>
              {vehicles.map((vehicle) => (
                <MenuItem key={vehicle.id} value={vehicle.id}>
                  {vehicle.displayName || `${vehicle.modell} / ${vehicle.kennzeichen}`}
                </MenuItem>
              ))}
            </Select>
            {(!vehicles || vehicles.length === 0) && (
              <FormHelperText error>
                Keine Fahrzeuge verfügbar. Bitte stellen Sie sicher, dass Fahrzeuge im System registriert sind.
              </FormHelperText>
            )}
          </FormControl>
        </Grid>

        {/* Mitarbeiter */}
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Mitarbeiter"
            name="employee_name"
            value={formData.employee_name}
            onChange={handleChange}
            required
          />
        </Grid>

        {/* Abteilung/Kostenstelle */}
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Abteilung/Kostenstelle"
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
          />
        </Grid>

        {/* Zweck */}
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Verwendungszweck"
            name="purpose"
            value={formData.purpose}
            onChange={handleChange}
            required
          />
        </Grid>

        {/* Datumsauswahl */}
        <Grid item xs={12} md={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="de">
            <DateTimePicker
              label="Startdatum"
              value={formData.start_date}
              onChange={(newValue) => handleDateChange('start_date', newValue)}
              renderInput={(params) => <TextField {...params} fullWidth required />}
              slotProps={{
                textField: { 
                  fullWidth: true,
                  required: true,
                  margin: "normal",
                  label: "Startdatum"
                }
              }}
            />
          </LocalizationProvider>
        </Grid>

        <Grid item xs={12} md={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="de">
            <DateTimePicker
              label="Enddatum"
              value={formData.end_date}
              onChange={(newValue) => handleDateChange('end_date', newValue)}
              renderInput={(params) => <TextField {...params} fullWidth required />}
              slotProps={{
                textField: { 
                  fullWidth: true,
                  required: true,
                  margin: "normal",
                  label: "Enddatum"
                }
              }}
            />
          </LocalizationProvider>
        </Grid>

        {/* Status */}
        <Grid item xs={12} md={6}>
          <FormControl fullWidth>
            <InputLabel id="status-label">Status</InputLabel>
            <Select
              labelId="status-label"
              id="status"
              name="status"
              value={formData.status}
              onChange={handleChange}
              label="Status"
            >
              <MenuItem value="Reserviert">Reserviert</MenuItem>
              <MenuItem value="Ausgegeben">Ausgegeben</MenuItem>
              <MenuItem value="Zurückgegeben">Zurückgegeben</MenuItem>
            </Select>
          </FormControl>
        </Grid>

        {/* Notizen */}
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Notizen"
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            multiline
            rows={4}
          />
        </Grid>

        {/* Buttons */}
        <Grid item xs={12}>
          <div className="flex justify-end space-x-2 mt-4">
            <Button
              variant="outlined"
              onClick={onClose}
              className="mr-2"
            >
              Abbrechen
            </Button>
            <Button
              type="submit"
              variant="contained"
              color="primary"
            >
              {booking ? 'Aktualisieren' : 'Buchung erstellen'}
            </Button>
          </div>
        </Grid>
      </Grid>
    </form>
  );
};

export default BookingForm;
