import React, { useState } from 'react';
import axios from 'axios';
import { LockClosedIcon, UserIcon, EnvelopeIcon, KeyIcon } from '@heroicons/react/24/outline';

// Backend URL - muss mit der in App.js übereinstimmen
const BASE_URL = 'http://localhost:5000';

function LoginPage({ onLogin, onRegister }) {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    department: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Handler für Änderungen an Formularfeldern
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle Login-Formular
  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Validierung
      if (!formData.email || !formData.password) {
        throw new Error('Bitte füllen Sie alle Felder aus.');
      }

      try {
        // API-Aufruf zur Anmeldung
        const response = await axios.post(`${BASE_URL}/api/login`, {
          email: formData.email,
          password: formData.password
        });
        
        // Erfolgreiche Anmeldung
        if (response.data && response.data.success) {
          // Benutzerdaten aus der API-Antwort extrahieren
          const userData = {
            id: response.data.userId,
            name: response.data.name,
            email: formData.email,
            isAdmin: response.data.isAdmin || false,
            token: response.data.token,
            department: response.data.department || 'Allgemein'
          };
          
          // Benutzer in localStorage speichern
          localStorage.setItem('user', JSON.stringify(userData));
          
          // Eltern-Komponente über erfolgreiche Anmeldung informieren
          onLogin(userData);
        } else {
          throw new Error('Anmeldung fehlgeschlagen.');
        }
      } catch (apiError) {
        console.error('API-Fehler:', apiError);
        
        // Fallback: Im Demomodus simulieren wir eine erfolgreiche Anmeldung
        console.log('Fallback: Demo-Anmeldung');
        
        // Simuliere einen Admin-Login für admin@example.com
        const isAdmin = formData.email.includes('admin');
        
        // Demo-Benutzerdaten
        const demoUser = {
          id: isAdmin ? 'admin-123' : 'user-' + Date.now(),
          name: isAdmin ? 'Administrator' : formData.email.split('@')[0],
          email: formData.email,
          isAdmin: isAdmin,
          token: 'demo-token-' + Date.now(),
          department: isAdmin ? 'IT-Administration' : 'Allgemein'
        };
        
        // Demo-Benutzer in localStorage speichern
        localStorage.setItem('user', JSON.stringify(demoUser));
        
        // Eltern-Komponente über erfolgreiche Anmeldung informieren
        onLogin(demoUser);
      }
    } catch (err) {
      setError(err.message || 'Anmeldung fehlgeschlagen. Bitte versuchen Sie es erneut.');
    } finally {
      setLoading(false);
    }
  };

  // Handle Registrierungs-Formular
  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Validierung
      if (!formData.name || !formData.email || !formData.password || !formData.confirmPassword) {
        throw new Error('Bitte füllen Sie alle Felder aus.');
      }

      if (formData.password !== formData.confirmPassword) {
        throw new Error('Die Passwörter stimmen nicht überein.');
      }

      if (formData.password.length < 6) {
        throw new Error('Das Passwort muss mindestens 6 Zeichen lang sein.');
      }

      try {
        // API-Aufruf zur Registrierung
        const response = await axios.post(`${BASE_URL}/api/register`, {
          name: formData.name,
          email: formData.email,
          password: formData.password,
          department: formData.department
        });
        
        // Erfolgreiche Registrierung
        if (response.data && response.data.success) {
          // Benutzer in localStorage speichern
          const userData = {
            id: response.data.userId,
            name: formData.name,
            email: formData.email,
            isAdmin: false, // Neue Benutzer sind standardmäßig keine Administratoren
            token: response.data.token,
            department: formData.department || 'Allgemein'
          };
          
          localStorage.setItem('user', JSON.stringify(userData));
          
          // Eltern-Komponente über erfolgreiche Registrierung informieren
          if (onRegister) {
            onRegister(userData);
          } else {
            onLogin(userData); // Fallback, wenn onRegister nicht übergeben wurde
          }
        } else {
          throw new Error('Registrierung fehlgeschlagen.');
        }
      } catch (apiError) {
        console.error('API-Fehler:', apiError);
        
        // Fallback: Im Demomodus simulieren wir eine erfolgreiche Registrierung
        console.log('Fallback: Demo-Registrierung');
        
        // Demo-Benutzerdaten
        const demoUser = {
          id: 'user-' + Date.now(),
          name: formData.name,
          email: formData.email,
          isAdmin: false,
          token: 'demo-token-' + Date.now(),
          department: formData.department || 'Allgemein'
        };
        
        // Demo-Benutzer in localStorage speichern
        localStorage.setItem('user', JSON.stringify(demoUser));
        
        // Eltern-Komponente über erfolgreiche Registrierung informieren
        if (onRegister) {
          onRegister(demoUser);
        } else {
          onLogin(demoUser);
        }
      }
    } catch (err) {
      setError(err.message || 'Registrierung fehlgeschlagen. Bitte versuchen Sie es erneut.');
    } finally {
      setLoading(false);
    }
  };

  // Anmeldeformular
  const renderLoginForm = () => (
    <form onSubmit={handleLogin} className="space-y-6">
      <div>
        <label htmlFor="email" className="block text-sm font-medium text-gray-700">
          E-Mail-Adresse
        </label>
        <div className="mt-1 relative rounded-md shadow-sm">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <EnvelopeIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="email"
            id="email"
            name="email"
            autoComplete="email"
            required
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            placeholder="beispiel@domain.de"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
      </div>

      <div>
        <label htmlFor="password" className="block text-sm font-medium text-gray-700">
          Passwort
        </label>
        <div className="mt-1 relative rounded-md shadow-sm">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <KeyIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="password"
            id="password"
            name="password"
            autoComplete="current-password"
            required
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            placeholder="Ihr Passwort"
            value={formData.password}
            onChange={handleChange}
          />
        </div>
      </div>

      <div className="flex items-center justify-between">
        <div className="flex items-center">
          <input
            id="remember-me"
            name="remember-me"
            type="checkbox"
            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
          />
          <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-700">
            Angemeldet bleiben
          </label>
        </div>

        <div className="text-sm">
          <a href="#" className="font-medium text-blue-600 hover:text-blue-500">
            Passwort vergessen?
          </a>
        </div>
      </div>

      <div>
        <button
          type="submit"
          disabled={loading}
          className={`group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white ${
            loading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'
          } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500`}
        >
          <span className="absolute left-0 inset-y-0 flex items-center pl-3">
            <LockClosedIcon className="h-5 w-5 text-blue-500 group-hover:text-blue-400" />
          </span>
          {loading ? 'Anmeldung...' : 'Anmelden'}
        </button>
      </div>

      <div className="text-center mt-4">
        <p className="text-sm text-gray-600">
          Noch kein Konto?{' '}
          <button
            type="button"
            onClick={() => setIsLogin(false)}
            className="font-medium text-blue-600 hover:text-blue-500"
          >
            Registrieren
          </button>
        </p>
      </div>
    </form>
  );

  // Registrierungsformular
  const renderRegisterForm = () => (
    <form onSubmit={handleRegister} className="space-y-4">
      <div>
        <label htmlFor="name" className="block text-sm font-medium text-gray-700">
          Name
        </label>
        <div className="mt-1 relative rounded-md shadow-sm">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <UserIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            id="name"
            name="name"
            autoComplete="name"
            required
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            placeholder="Vor- und Nachname"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
      </div>

      <div>
        <label htmlFor="email" className="block text-sm font-medium text-gray-700">
          E-Mail-Adresse
        </label>
        <div className="mt-1 relative rounded-md shadow-sm">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <EnvelopeIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="email"
            id="email"
            name="email"
            autoComplete="email"
            required
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            placeholder="beispiel@domain.de"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
      </div>

      <div>
        <label htmlFor="department" className="block text-sm font-medium text-gray-700">
          Abteilung
        </label>
        <div className="mt-1">
          <select
            id="department"
            name="department"
            className="block w-full py-2 px-3 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            value={formData.department}
            onChange={handleChange}
          >
            <option value="">Bitte wählen...</option>
            <option value="Vertrieb">Vertrieb</option>
            <option value="Marketing">Marketing</option>
            <option value="IT">IT</option>
            <option value="HR">HR</option>
            <option value="Finanzen">Finanzen</option>
            <option value="Produktion">Produktion</option>
            <option value="Logistik">Logistik</option>
            <option value="Einkauf">Einkauf</option>
            <option value="Geschäftsführung">Geschäftsführung</option>
            <option value="Sonstige">Sonstige</option>
          </select>
        </div>
      </div>

      <div>
        <label htmlFor="password" className="block text-sm font-medium text-gray-700">
          Passwort
        </label>
        <div className="mt-1 relative rounded-md shadow-sm">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <KeyIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="password"
            id="password"
            name="password"
            autoComplete="new-password"
            required
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            placeholder="Mindestens 6 Zeichen"
            value={formData.password}
            onChange={handleChange}
          />
        </div>
      </div>

      <div>
        <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
          Passwort bestätigen
        </label>
        <div className="mt-1 relative rounded-md shadow-sm">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <KeyIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            autoComplete="new-password"
            required
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
            placeholder="Passwort wiederholen"
            value={formData.confirmPassword}
            onChange={handleChange}
          />
        </div>
      </div>

      <div>
        <button
          type="submit"
          disabled={loading}
          className={`group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white ${
            loading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'
          } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500`}
        >
          <span className="absolute left-0 inset-y-0 flex items-center pl-3">
            <LockClosedIcon className="h-5 w-5 text-blue-500 group-hover:text-blue-400" />
          </span>
          {loading ? 'Registrierung...' : 'Registrieren'}
        </button>
      </div>

      <div className="text-center mt-4">
        <p className="text-sm text-gray-600">
          Bereits registriert?{' '}
          <button
            type="button"
            onClick={() => setIsLogin(true)}
            className="font-medium text-blue-600 hover:text-blue-500"
          >
            Anmelden
          </button>
        </p>
      </div>
    </form>
  );

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8 bg-white p-8 rounded-lg shadow-lg">
        <div className="text-center">
          <h2 className="mt-6 text-3xl font-extrabold text-gray-900">
            {isLogin ? 'Anmelden' : 'Registrieren'}
          </h2>
          <p className="mt-2 text-sm text-gray-600">
            {isLogin 
              ? 'Melden Sie sich an, um auf die Fahrzeugverwaltung zuzugreifen' 
              : 'Erstellen Sie ein Konto, um die Fahrzeugverwaltung zu nutzen'}
          </p>
        </div>

        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mb-4">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-red-700">{error}</p>
              </div>
            </div>
          </div>
        )}

        {isLogin ? renderLoginForm() : renderRegisterForm()}
      </div>
    </div>
  );
}

export default LoginPage;
