import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import VehicleList from './components/VehicleList';
import GlobalDashboard from './components/GlobalDashboard';
import StatisticsDashboard from './components/StatisticsDashboard';
import UserDashboard from './components/UserDashboard';
import VehicleRequests from './components/VehicleRequests';
import UserRequests from './components/UserRequests';
import axios from 'axios';

// Konstante für die API-URL - angepasst, vermutlich ohne '/api' Präfix
const API_URL = 'http://localhost:5000';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(true); // Für Demo-Zwecke standardmäßig angemeldet
  const [isAdmin, setIsAdmin] = useState(true); // Für Demo-Zwecke standardmäßig Admin
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeView, setActiveView] = useState('vehicles'); // Standard: Fahrzeugansicht anzeigen
  
  // Zentraler Fahrzeugzustand
  const [vehicles, setVehicles] = useState([]);
  const [vehiclesLoaded, setVehiclesLoaded] = useState(false);
  const [loadingError, setLoadingError] = useState(null);
  
  // Laden der Fahrzeuge beim Start der Anwendung
  useEffect(() => {
    loadVehicles();
  }, []);
  
  // Funktion zum Laden der Fahrzeuge direkt aus der API
  const loadVehicles = () => {
    console.log("App: Lade Fahrzeuge aus der API...");
    setVehiclesLoaded(false);
    setLoadingError(null);
    
    // Endpunkte angepasst auf verschiedene mögliche Varianten
    // Versuche zuerst den Pfad '/fahrzeuge'
    axios.get(`${API_URL}/fahrzeuge`)
      .then(response => {
        handleSuccessfulVehicleLoad(response);
      })
      .catch(error => {
        console.log("Fehler bei /fahrzeuge, versuche alternatives Format...");
        
        // Falls nicht erfolgreich, versuche '/api/fahrzeuge'
        axios.get(`${API_URL}/api/fahrzeuge`)
          .then(response => {
            handleSuccessfulVehicleLoad(response);
          })
          .catch(error2 => {
            console.log("Fehler bei /api/fahrzeuge, versuche alternatives Format...");
            
            // Falls nicht erfolgreich, versuche '/fahrzeug' (ohne s)
            axios.get(`${API_URL}/fahrzeug`)
              .then(response => {
                handleSuccessfulVehicleLoad(response);
              })
              .catch(error3 => {
                // Falls alle fehlschlagen, zeige Fehler an
                console.error('App: Alle Versuche, Fahrzeuge zu laden, sind fehlgeschlagen');
                logDetailedError(error3, "Laden der Fahrzeuge");
                setLoadingError(`Fehler beim Laden der Fahrzeuge: Keiner der Endpunkte funktioniert`);
                setVehicles([]);
                setVehiclesLoaded(true);
              });
          });
      });
  };
  
  // Hilfsfunktion für das erfolgreiche Laden von Fahrzeugen
  const handleSuccessfulVehicleLoad = (response) => {
    console.log('Antwort vom Server beim Laden von Fahrzeugen:', response);
    
    if (response.data && Array.isArray(response.data)) {
      console.log(`App: ${response.data.length} Fahrzeuge aus der API geladen:`, response.data);
      setVehicles(response.data);
    } else if (response.data && typeof response.data === 'object' && !Array.isArray(response.data)) {
      // Falls die Antwort ein einzelnes Objekt ist oder eine spezielle Struktur hat
      console.log("API-Antwort ist ein Objekt, keine Array. Versuche Daten zu extrahieren:", response.data);
      
      // Versuche, ein Array aus der Antwort zu extrahieren (häufige Varianten)
      let extractedData = null;
      if (response.data.vehicles) extractedData = response.data.vehicles;
      else if (response.data.fahrzeuge) extractedData = response.data.fahrzeuge;
      else if (response.data.data) extractedData = response.data.data;
      else if (response.data.results) extractedData = response.data.results;
      
      if (extractedData && Array.isArray(extractedData)) {
        console.log("Extrahierte Fahrzeugdaten:", extractedData);
        setVehicles(extractedData);
      } else {
        console.warn("Konnte keine Fahrzeugdaten aus der Antwort extrahieren");
        setLoadingError("Fehler beim Laden der Fahrzeuge: Unerwartetes Datenformat");
        setVehicles([]);
      }
    } else {
      console.warn("App: Unerwartetes Datenformat von der API:", response.data);
      setLoadingError("Fehler beim Laden der Fahrzeuge: Unerwartetes Datenformat");
      setVehicles([]);
    }
    
    setVehiclesLoaded(true);
  };
  
  // Hilfsfunktion für detaillierte Fehlerprotokolle
  const logDetailedError = (error, operation) => {
    console.error(`App: Fehler beim ${operation}:`, error);
    
    if (error.response) {
      console.error("Status:", error.response.status);
      console.error("Daten:", error.response.data);
      console.error("Headers:", error.response.headers);
    } else if (error.request) {
      console.error("Keine Antwort erhalten:", error.request);
    } else {
      console.error("Fehler beim Erstellen der Anfrage:", error.message);
    }
  };

  // Fahrzeug hinzufügen
  const addVehicle = (newVehicle) => {
    console.log("App: Füge neues Fahrzeug hinzu:", newVehicle);
    
    // FormData für Dateiupload erstellen
    const formData = new FormData();
    
    // Alle Eigenschaften des Fahrzeugs hinzufügen
    Object.entries(newVehicle).forEach(([key, value]) => {
      if (key === 'bild' && value instanceof File) {
        formData.append('bild', value);
      } else if (value !== undefined && value !== null) {
        formData.append(key, value);
      }
    });
    
    // Debug-Ausgabe der FormData
    console.log("Sende Daten an API:");
    for (let pair of formData.entries()) {
      console.log(pair[0] + ': ' + pair[1]);
    }
    
    // Versuche verschiedene Endpunkte für das Hinzufügen
    tryAddVehicle(`${API_URL}/fahrzeug`, formData)
      .catch(error => {
        console.log("Fehler bei /fahrzeug, versuche alternatives Format...");
        return tryAddVehicle(`${API_URL}/api/fahrzeug`, formData);
      })
      .catch(error => {
        console.log("Fehler bei /api/fahrzeug, versuche alternatives Format...");
        return tryAddVehicle(`${API_URL}/fahrzeuge`, formData);
      })
      .catch(error => {
        console.error("Alle Versuche, das Fahrzeug hinzuzufügen, sind fehlgeschlagen");
        logDetailedError(error, "Hinzufügen des Fahrzeugs");
        alert(`Fehler beim Hinzufügen des Fahrzeugs: Keiner der Endpunkte funktioniert`);
      });
  };
  
  // Hilfsfunktion zum Versuch, ein Fahrzeug mit verschiedenen Endpunkten hinzuzufügen
  const tryAddVehicle = (url, formData) => {
    return axios.post(url, formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
    .then(response => {
      console.log("App: Fahrzeug erfolgreich über API hinzugefügt:", response.data);
      // Nach erfolgreicher Hinzufügung alle Fahrzeuge neu laden
      loadVehicles();
      alert("Fahrzeug erfolgreich hinzugefügt!");
    });
  };
  
  // Fahrzeug aktualisieren
  const updateVehicle = (updatedVehicle) => {
    console.log("App: Aktualisiere Fahrzeug:", updatedVehicle);
    
    // FormData für Dateiupload erstellen (falls benötigt)
    const formData = new FormData();
    
    // Alle Eigenschaften des Fahrzeugs hinzufügen
    Object.entries(updatedVehicle).forEach(([key, value]) => {
      if (key === 'bild' && value instanceof File) {
        formData.append('bild', value);
      } else if (value !== undefined && value !== null) {
        formData.append(key, value);
      }
    });
    
    // API-Anfrage senden
    axios.put(`${API_URL}/fahrzeug/${updatedVehicle.id}`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
      .then(response => {
        console.log("App: Fahrzeug erfolgreich über API aktualisiert:", response.data);
        // Nach erfolgreicher Aktualisierung alle Fahrzeuge neu laden
        loadVehicles();
        alert("Fahrzeug erfolgreich aktualisiert!");
      })
      .catch(error => {
        console.error("App: Fehler beim Aktualisieren des Fahrzeugs über API:", error);
        if (error.response) {
          alert(`Fehler beim Aktualisieren des Fahrzeugs: ${error.response.status} ${error.response.statusText}`);
        } else if (error.request) {
          alert("Fehler beim Aktualisieren des Fahrzeugs: Server antwortet nicht");
        } else {
          alert(`Fehler beim Aktualisieren des Fahrzeugs: ${error.message}`);
        }
      });
  };
  
  // Fahrzeug löschen
  const deleteVehicle = (vehicleId) => {
    console.log("App: Lösche Fahrzeug mit ID:", vehicleId);
    
    // API-Anfrage senden
    axios.delete(`${API_URL}/fahrzeug/${vehicleId}`)
      .then(response => {
        console.log("App: Fahrzeug erfolgreich über API gelöscht");
        // Nach erfolgreicher Löschung alle Fahrzeuge neu laden
        loadVehicles();
        alert("Fahrzeug erfolgreich gelöscht!");
      })
      .catch(error => {
        console.error("App: Fehler beim Löschen des Fahrzeugs über API:", error);
        if (error.response) {
          alert(`Fehler beim Löschen des Fahrzeugs: ${error.response.status} ${error.response.statusText}`);
        } else if (error.request) {
          alert("Fehler beim Löschen des Fahrzeugs: Server antwortet nicht");
        } else {
          alert(`Fehler beim Löschen des Fahrzeugs: ${error.message}`);
        }
      });
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('isLoggedIn');
  };

  const toggleAdminMode = () => {
    setIsAdmin(!isAdmin);
  };

  // Renderlogik basierend auf der aktiven Ansicht
  const renderContent = () => {
    if (!vehiclesLoaded) {
      return (
        <div className="w-full flex flex-col items-center justify-center my-12">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mb-4"></div>
          <p className="text-gray-600">Fahrzeuge werden geladen...</p>
        </div>
      );
    }

    if (loadingError) {
      return (
        <div className="w-full flex flex-col items-center justify-center my-12 text-red-500">
          <p>{loadingError}</p>
          <button 
            onClick={loadVehicles} 
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Erneut versuchen
          </button>
        </div>
      );
    }

    switch (activeView) {
      case 'dashboard':
        return isAdmin ? <GlobalDashboard /> : <UserDashboard />;
      case 'vehicles':
        return <VehicleList 
          isUserView={!isAdmin} 
          vehicles={vehicles} 
          onAddVehicle={addVehicle} 
          onUpdateVehicle={updateVehicle} 
          onDeleteVehicle={deleteVehicle} 
        />;
      case 'statistics':
        return isAdmin ? <StatisticsDashboard /> : null;
      case 'vehicle-requests':
        return isAdmin ? <VehicleRequests /> : null;
      case 'my-requests':
        return !isAdmin ? <UserRequests setActiveView={setActiveView} /> : null;
      default:
        return <VehicleList 
          isUserView={!isAdmin} 
          vehicles={vehicles} 
          onAddVehicle={addVehicle} 
          onUpdateVehicle={updateVehicle} 
          onDeleteVehicle={deleteVehicle} 
        />;
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar 
        isOpen={sidebarOpen} 
        toggleSidebar={() => setSidebarOpen(!sidebarOpen)} 
        isAdmin={isAdmin}
        onLogout={handleLogout}
        activeView={activeView}
        setActiveView={setActiveView}
      />
      
      <div className="flex-1 overflow-auto">
        <div className="p-4 md:p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-gray-800">Fahrzeugverwaltung</h1>
            
            <div className="flex items-center">
              <span className="mr-2 text-sm">{isAdmin ? 'Admin-Modus' : 'Benutzer-Modus'}</span>
              <button 
                onClick={toggleAdminMode}
                className="px-3 py-1 text-sm bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                {isAdmin ? 'Zu Benutzeransicht wechseln' : 'Zu Admin-Ansicht wechseln'}
              </button>
            </div>
          </div>
          
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default App;
