          console.log("App: Fahrzeuge im localStorage aktualisiert");
        } catch (error) {
          console.error("App: Fehler beim Speichern im localStorage:", error);
        }
      })
      .catch(error => {
        console.error("App: Fehler beim Hinzufügen des Fahrzeugs über API:", error);
        
        // Fallback: Lokales Hinzufügen
        const vehicleWithId = {
          ...newVehicle,
          id: Date.now() // In der realen Anwendung würde die ID vom Server kommen
        };
        
        const updatedVehicles = [...vehicles, vehicleWithId];
        setVehicles(updatedVehicles);
        
        // Speichern im localStorage
        try {
          localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
          console.log("App: Fahrzeuge im localStorage aktualisiert (Fallback)");
        } catch (error) {
          console.error("App: Fehler beim Speichern im localStorage:", error);
        }
      });
    }
  };
  
  // Fahrzeug aktualisieren
  const updateVehicle = (updatedVehicle) => {
    console.log("App: Aktualisiere Fahrzeug:", updatedVehicle);
    
    // Versuche, das Fahrzeug über die API zu aktualisieren
    axios.put(`http://localhost:5000/api/vehicles/${updatedVehicle.id}`, updatedVehicle)
      .then(response => {
        console.log("App: Fahrzeug erfolgreich über API aktualisiert:", response.data);
        
        // Lokalen State aktualisieren
        const updatedVehicles = vehicles.map(v => v.id === updatedVehicle.id ? response.data : v);
        setVehicles(updatedVehicles);
        
        // Speichern im localStorage
        try {
          localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
          console.log("App: Fahrzeuge im localStorage aktualisiert");
        } catch (error) {
          console.error("App: Fehler beim Speichern im localStorage:", error);
        }
      })
      .catch(error => {
        console.error("App: Fehler beim Aktualisieren des Fahrzeugs über API:", error);
        
        // Fallback: Lokale Aktualisierung
        const updatedVehicles = vehicles.map(v => v.id === updatedVehicle.id ? updatedVehicle : v);
        setVehicles(updatedVehicles);
        
        // Speichern im localStorage
        try {
          localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
          console.log("App: Fahrzeuge im localStorage aktualisiert (Fallback)");
        } catch (error) {
          console.error("App: Fehler beim Speichern im localStorage:", error);
        }
      });
  };
  
  // Fahrzeug löschen
  const deleteVehicle = (vehicleId) => {
    console.log("App: Lösche Fahrzeug mit ID:", vehicleId);
    
    // Versuche, das Fahrzeug über die API zu löschen
    axios.delete(`http://localhost:5000/api/vehicles/${vehicleId}`)
      .then(response => {
        console.log("App: Fahrzeug erfolgreich über API gelöscht");
        
        // Lokalen State aktualisieren
        const updatedVehicles = vehicles.filter(v => v.id !== vehicleId);
        setVehicles(updatedVehicles);
        
        // Speichern im localStorage
        try {
          localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
          console.log("App: Fahrzeuge im localStorage aktualisiert");
        } catch (error) {
          console.error("App: Fehler beim Speichern im localStorage:", error);
        }
      })
      .catch(error => {
        console.error("App: Fehler beim Löschen des Fahrzeugs über API:", error);
        
        // Fallback: Lokale Löschung
        const updatedVehicles = vehicles.filter(v => v.id !== vehicleId);
        setVehicles(updatedVehicles);
        
        // Speichern im localStorage
        try {
          localStorage.setItem('vehicles', JSON.stringify(updatedVehicles));
          console.log("App: Fahrzeuge im localStorage aktualisiert (Fallback)");
        } catch (error) {
          console.error("App: Fehler beim Speichern im localStorage:", error);
        }
      });
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('isLoggedIn');
  };

  const toggleAdminMode = () => {
    setIsAdmin(!isAdmin);
  };

  // Renderlogik basierend auf der aktiven Ansicht
  const renderContent = () => {
    if (!vehiclesLoaded) {
      return <div className="w-full flex justify-center my-12">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>;
    }

    switch (activeView) {
      case 'dashboard':
        return isAdmin ? <GlobalDashboard /> : <UserDashboard />;
      case 'vehicles':
        return <VehicleList 
          isUserView={!isAdmin} 
          vehicles={vehicles} 
          onAddVehicle={addVehicle} 
          onUpdateVehicle={updateVehicle} 
          onDeleteVehicle={deleteVehicle} 
        />;
      case 'statistics':
        return isAdmin ? <StatisticsDashboard /> : null;
      case 'vehicle-requests':
        return isAdmin ? <VehicleRequests /> : null;
      case 'my-requests':
        return !isAdmin ? <UserRequests setActiveView={setActiveView} /> : null;
      default:
        return <VehicleList 
          isUserView={!isAdmin} 
          vehicles={vehicles} 
          onAddVehicle={addVehicle} 
          onUpdateVehicle={updateVehicle} 
          onDeleteVehicle={deleteVehicle} 
        />;
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar 
        isOpen={sidebarOpen} 
        toggleSidebar={() => setSidebarOpen(!sidebarOpen)} 
        isAdmin={isAdmin}
        onLogout={handleLogout}
        activeView={activeView}
        setActiveView={setActiveView}
      />
      
      <div className="flex-1 overflow-auto">
        <div className="p-4 md:p-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-bold text-gray-800">Fahrzeugverwaltung</h1>
            
            <div className="flex items-center">
              <span className="mr-2 text-sm">{isAdmin ? 'Admin-Modus' : 'Benutzer-Modus'}</span>
              <button 
                onClick={toggleAdminMode}
                className="px-3 py-1 text-sm bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                {isAdmin ? 'Zu Benutzeransicht wechseln' : 'Zu Admin-Ansicht wechseln'}
              </button>
            </div>
          </div>
          
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default App;
