import React, { useState } from 'react';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import { TextField } from '@mui/material';
import dayjs from 'dayjs';
import 'dayjs/locale/de';

dayjs.locale('de');

function BookingForm({ onClose, onSubmit, booking, vehicles = [], initialDate }) {
  console.log('VerfÃ¼gbare Fahrzeuge:', vehicles); // Debug-Log

  const [formData, setFormData] = useState({
    vehicle_id: booking?.vehicle_id || '',
    employee_name: booking?.employee_name || '',
    department: booking?.department || '',
    purpose: booking?.purpose || '',
    start_date: booking?.start || initialDate || dayjs(),
    end_date: booking?.end || initialDate || dayjs(),
    status: booking?.status || 'Reserviert',
    notes: booking?.notes || '',
    start_mileage: booking?.start_mileage || '',
    start_fuel_level: booking?.start_fuel_level || '',
    key_number: booking?.key_number || ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      start_date: formData.start_date.toISOString(),
      end_date: formData.end_date.toISOString()
    });
  };

  const handleCancel = () => {
    if (onClose) {
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-bold">
            {booking ? 'Buchung bearbeiten' : 'Neue Buchung'}
          </h2>
          <button
            type="button"
            onClick={handleCancel}
            className="text-gray-500 hover:text-gray-700"
          >
            <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Fahrzeug
              </label>
              <select
                name="vehicle_id"
                value={formData.vehicle_id}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 bg-white py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
                required
              >
                <option value="">Fahrzeug auswÃ¤hlen</option>
                {Array.isArray(vehicles) && vehicles.map(vehicle => (
                  <option key={vehicle.id} value={vehicle.id}>
                    {vehicle.kennzeichen} - {vehicle.modell}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Mitarbeiter
              </label>
              <input
                type="text"
                name="employee_name"
                value={formData.employee_name}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Abteilung/Kostenstelle
              </label>
              <input
                type="text"
                name="department"
                value={formData.department}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Verwendungszweck
              </label>
              <input
                type="text"
                name="purpose"
                value={formData.purpose}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
                required
              />
            </div>

            <LocalizationProvider dateAdapter={AdapterDayjs} adapterLocale="de">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Start
                </label>
                <DateTimePicker
                  value={dayjs(formData.start_date)}
                  onChange={(newValue) => setFormData(prev => ({ ...prev, start_date: newValue }))}
                  className="w-full"
                  slotProps={{ 
                    textField: { 
                      variant: 'outlined',
                      size: 'small',
                      fullWidth: true,
                      className: "bg-white"
                    } 
                  }}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ende
                </label>
                <DateTimePicker
                  value={dayjs(formData.end_date)}
                  onChange={(newValue) => setFormData(prev => ({ ...prev, end_date: newValue }))}
                  minDateTime={dayjs(formData.start_date)}
                  className="w-full"
                  slotProps={{ 
                    textField: { 
                      variant: 'outlined',
                      size: 'small',
                      fullWidth: true,
                      className: "bg-white"
                    } 
                  }}
                />
              </div>
            </LocalizationProvider>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Status
              </label>
              <select
                name="status"
                value={formData.status}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 bg-white py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
              >
                <option value="Reserviert">Reserviert</option>
                <option value="Ausgegeben">Ausgegeben</option>
                <option value="ZurÃ¼ckgegeben">ZurÃ¼ckgegeben</option>
              </select>
            </div>

            {formData.status === 'Ausgegeben' && (
              <>
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Kilometerstand bei Ausgabe
                  </label>
                  <input
                    type="number"
                    name="start_mileage"
                    value={formData.start_mileage}
                    onChange={handleChange}
                    className="mt-1 block w-full rounded-md border border-gray-300 py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Tankstand bei Ausgabe (%)
                  </label>
                  <input
                    type="number"
                    name="start_fuel_level"
                    value={formData.start_fuel_level}
                    onChange={handleChange}
                    min="0"
                    max="100"
                    className="mt-1 block w-full rounded-md border border-gray-300 py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    SchlÃ¼sselnummer
                  </label>
                  <input
                    type="text"
                    name="key_number"
                    value={formData.key_number}
                    onChange={handleChange}
                    className="mt-1 block w-full rounded-md border border-gray-300 py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
                  />
                </div>
              </>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Notizen
            </label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              rows="3"
              className="mt-1 block w-full rounded-md border border-gray-300 py-2 px-3 shadow-sm focus:border-blue-500 focus:outline-none focus:ring-blue-500 sm:text-sm"
            />
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={handleCancel}
              className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-gray-700 bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
            >
              Abbrechen
            </button>
            <button
              type="submit"
              className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              {booking ? 'Aktualisieren' : 'Buchung erstellen'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default BookingForm;
